// Define the SearchResult type to represent both MovieSearchResult and TvShowSearchResult
type SearchResult = MovieSearchResult | TvShowSearchResult;

// Define interfaces for search results
interface SearchResults<T> {
  page: number;
  results: T[];
  total_results: number;
  total_pages: number;
}

interface MovieSearchResult {
  adult: boolean;
  backdrop_path: string | null;
  genre_ids: number[];
  id: number;
  original_language: string;
  original_title: string;
  overview: string;
  popularity: number;
  poster_path: string | null;
  release_date: string;
  title: string;
  video: boolean;
  vote_average: number;
  vote_count: number;
}

interface TvShowSearchResult {
  adult: boolean;
  backdrop_path: string | null;
  genre_ids: number[];
  id: number;
  origin_country: string[];
  original_language: string;
  original_name: string;
  overview: string;
  popularity: number;
  poster_path: string | null;
  first_air_date: string;
  name: string;
  vote_average: number;
  vote_count: number;
}
